// import { ParallaxLayer } from '@react-spring/parallax';
import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col, CardGroup, Card, Button } from 'react-bootstrap';
import Breadcrumbs from './Breadcrumbs';
// import Phone from '../assets/telephone.jpg';
import projects from '../projects';

const Portfolio = ({ parallax }) => {
  const [isShown, setIsShown] = useState(false);
  const ref = useRef(parallax);

  useEffect(() => {
    const mouseLeaveEvent = (e) => {
      e.preventDefault();
    }

    // If dropdown is open, listen for mouse hovering out of area
    if (isShown) {
      window.addEventListener('mouseover', mouseLeaveEvent);
    }

    return () => {
      window.removeEventListener('mouseover', mouseLeaveEvent);
    }
  }, [isShown]);

  return (
    <>
      <div className='section-header' id='portfolio'>
        <h1 onMouseEnter={() => setIsShown(true)}>My Work</h1>
        {isShown &&
          <Breadcrumbs setIsShown={setIsShown} parallax={ref} />
        }
      </div>
      <Container>
        <Row className='section-content'>
          <Col className='section-text'>
            <CardGroup>
              {projects.map(project => (
                <Card key={project.title}>
                  <Card.Image variant="top" src={project.image}>
                      <Card.Body>
                          <Card.Title>{project.title}</Card.Title>
                          <Card.Text>
                              {project.description}
                          </Card.Text>
                          <Button href={project.siteLink}>Check out this project!</Button>
                      </Card.Body>
                  </Card.Image>
                </Card>
              ))}
            </CardGroup>
          </Col>
          {/* <Col>
            <img src={Phone} fluid={true} className='section-image' alt='Old Phone Vectors by Vecteezy' />
          </Col> */}
        </Row>
      </Container>
    </>

  )
};

export default Portfolio;
