import { ParallaxLayer } from '@react-spring/parallax';
import React, { useRef } from 'react';
import { Breadcrumb, BreadcrumbItem } from 'react-bootstrap';
import { Link } from 'react-scroll';

const Breadcrumbs = ({ setIsShown, parallax }) => {
    const ref = useRef(parallax);
    console.log(ref.current);

    return (
        <Breadcrumb onMouseLeave={() => setIsShown(false)} id='breadcrumb-container'>
            <BreadcrumbItem linkAs='h3' onClick={() => ref.current.current.scrollTo(0)}>
                Home
            </BreadcrumbItem>
            <BreadcrumbItem linkAs='h3'>
                <Link
                    to='about'
                    activeClass='active'
                    spy={true}
                    duration={1000}
                    smooth={true}
                >
                    About Me
                </Link>
            </BreadcrumbItem>
            <BreadcrumbItem linkAs='h3'>
                <Link
                    to='portfolio'
                    activeClass='active'
                    spy={true}
                    duration={1000}
                    smooth={true}
                >
                    My Work
                </Link>
            </BreadcrumbItem>
            <BreadcrumbItem linkAs='h3'>
                <Link
                    to='contact'
                    activeClass='active'
                    spy={true}
                    duration={1000}
                    smooth={true}
                >
                    Contact
                </Link>
            </BreadcrumbItem>
        </Breadcrumb>
    )
};

export default Breadcrumbs;
