import React, { useState, useEffect } from 'react';
import { Breadcrumb, BreadcrumbItem } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';

const Breadcrumbs = () => {
    // Get the active page from the URL
    const location = useLocation();
    const path = location.pathname;

    // // Used for tracking what page user is currently on so that link is hidden
    const [showLink, setShowLink] = useState(true);




    return (
        <Breadcrumb>
                <BreadcrumbItem className='home-nav-link'>
                    <Link to='/' className='breadcrumb'><h3>Home</h3></Link>
                </BreadcrumbItem>
                    <BreadcrumbItem active={path === '/about' ? true : false} className="home-nav-link">
                        <Link to='/about' className='breadcrumb'><h3>About Me</h3></Link>
                    </BreadcrumbItem>
                    <BreadcrumbItem active={path === '/portfolio' ? true : false} className="home-nav-link">
                        <Link to='/portfolio' className='breadcrumb'><h3>My Work</h3></Link>
                    </BreadcrumbItem>
                    <BreadcrumbItem active={path === '/contact' ? true : false} className="home-nav-link">
                        <Link to='/contact' className='breadcrumb'><h3>Contact</h3></Link>
                    </BreadcrumbItem>
        </Breadcrumb>
    )
};

export default Breadcrumbs;
